import React, { useState, useEffect } from "react";
import { Box, Button, TextField, Switch } from "@material-ui/core";
import PersonOutlineOutlinedIcon from "@mui/icons-material/PersonOutlineOutlined";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import LocalPhoneIcon from "@mui/icons-material/LocalPhone";
import LocationCityIcon from "@mui/icons-material/LocationCity";
import axios from "axios";
import logo from "../assets/discuss.png";
import "../styles/form.css";

export default function FormPage() {

  const [loginData, setLoginData] = useState({
    name:"",
    contact:"",
    city:"",
    email: "",
    pass: "",
    cpass: "",
  });

  const [togglePass, setTogglePass] = useState(true);

  useEffect(() => {}, [togglePass]);

  let name, val;
  const handleInput = (e) => {
    name = e.target.name;
    val = e.target.value;
    setLoginData({ ...loginData, [name]: val });
    // console.log(loginData);
  };


  const handleSubmit = async (e) => {
    e.preventDefault();

    if (
      loginData.email === "" ||
      loginData.pass === "" ||
      loginData.cpass === "" ||
      loginData.name === "" ||
      loginData.city === "" ||
      loginData.contact === ""
    ) {
    //   alert("Fields can't be empty!!");
    toast("Fields can't be empty!!");
    }

     if (loginData.pass === "" || loginData.cpass === "") {
       alert("Password and Confirm Password do not match!!");
     }
